import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonIcon, IonButton } from '@ionic/react';
import { informationCircleOutline } from 'ionicons/icons';
import React, { useState } from 'react';
import { BleClient, numbersToDataView } from '@capacitor-community/bluetooth-le';

import './Home.css';

import Titulo_Posiciones from '../imagenes/Titulo_Posiciones.svg';
import Modo_TV from '../imagenes/Modo_TV.svg';
import Modo_siesta from '../imagenes/Modo_siesta.svg';
import Modo_lectura from '../imagenes/Modo_lectura.svg';
import Posición_cerrado from '../imagenes/Posición_cerrado.svg';

const HEART_RATE_SERVICE = '143fda37-5a0e-4ebb-9a3c-fc0e7625f086';
const CHAR_DIR_UUID = "ceba6cdc-8e39-42f1-a8b8-fca3832014fe"

import { useHistory } from 'react-router';

function Sillones(){
  
  const history = useHistory();

  const openWeb = () => {
    window.open('https://www.fama.es/sensae', '_blank', 'location=yes');
  };

  async function parar(){
    try {

      if(BleClient.getConnectedDevices([]) === 0){
      
        history.push('/Sillones')
  
      }else{
  
        const devices = await BleClient.getConnectedDevices([]);
        console.log('Dispositivos conectados:', devices);
  
        devices.forEach(async (device) => {
          await BleClient.write(device.deviceId, HEART_RATE_SERVICE, CHAR_DIR_UUID, numbersToDataView([0]));
          console.log('written [1, 0] to control point');
  
        });
      }

    } catch (error) {
      console.error(error);
    }
  }

  async function Lectura(){

    try {

      if(BleClient.getConnectedDevices([]) === 0){
      
        history.push('/Sillones')
  
      }else{
  
        const devices = await BleClient.getConnectedDevices([]);
        console.log('Dispositivos conectados:', devices);
  
        devices.forEach(async (device) => {
          await BleClient.write(device.deviceId, HEART_RATE_SERVICE, CHAR_DIR_UUID, numbersToDataView([1]));
          console.log('written [1, 0] to control point');
        });

        setTimeout(() => {
          parar();
        }, 3000);

      }

    } catch (error) {
      console.error(error);
    }

  }

  async function TV(){

    try {

      if(BleClient.getConnectedDevices([]) === 0){
      
        history.push('/Sillones')
  
      }else{
  
        const devices = await BleClient.getConnectedDevices([]);
        console.log('Dispositivos conectados:', devices);
  
        devices.forEach(async (device) => {
          await BleClient.write(device.deviceId, HEART_RATE_SERVICE, CHAR_DIR_UUID, numbersToDataView([1]));
          console.log('written [1, 0] to control point');
  
        });

        setTimeout(parar, 7000);

      }

    } catch (error) {
      console.error(error);
    }

  }

  async function Siesta(){

    try {

      if(BleClient.getConnectedDevices([]) === 0){
      
        history.push('/Sillones')
  
      }else{
  
        const devices = await BleClient.getConnectedDevices([]);
        console.log('Dispositivos conectados:', devices);
  
        devices.forEach(async (device) => {
          await BleClient.write(device.deviceId, HEART_RATE_SERVICE, CHAR_DIR_UUID, numbersToDataView([1]));
          console.log('written [1, 0] to control point');
  
        });

        setTimeout(parar, 13000);

      }

    } catch (error) {
      console.error(error);
    }

  }

  async function Cerrado(){

    try {

      if(BleClient.getConnectedDevices([]) === 0){
      
        history.push('/Sillones')
  
      }else{
  
        const devices = await BleClient.getConnectedDevices([]);
        console.log('Dispositivos conectados:', devices);
  
        devices.forEach(async (device) => {
          await BleClient.write(device.deviceId, HEART_RATE_SERVICE, CHAR_DIR_UUID, numbersToDataView([-1]));
          console.log('written [1, 0] to control point');
  
        });

        setTimeout(parar, 13000);

      }

    } catch (error) {
      console.error(error);
    }

  }

  return (
    <IonPage>
      <IonHeader className='toolbar'>
        <IonToolbar className='toolbar'>
          <IonTitle className='titulo3'>Sillón Conectado
          <button  className='buttonInfo' onClick={openWeb}><IonIcon icon={informationCircleOutline}></IonIcon></button></IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>
        
        <img src={Titulo_Posiciones} className='TituloPosiciones'></img>
        <img src={Modo_lectura} className='imagenesPosiciones' onClick={Lectura}></img>
        <img src={Modo_TV} className='imagenesPosiciones' onClick={TV}></img>
        <img src={Modo_siesta} className='imagenesPosiciones' onClick={Siesta}></img>
        <img src={Posición_cerrado} className='imagenesPosiciones' onClick={Cerrado}></img>


    

      </IonContent>
    </IonPage>
  );
};

export default Sillones;
