import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonIcon, IonButton } from '@ionic/react';
import { informationCircleOutline } from 'ionicons/icons';
import { BleClient, ScanMode, numbersToDataView } from '@capacitor-community/bluetooth-le';
import React, { useState, useEffect } from 'react';

import './Home.css';

import Una_Mano from '../imagenes/A_una_mano.svg';
import Una_Mano_Selected from '../imagenes/A_una_mano_Selected.svg';
import Dos_Manos from '../imagenes/A_dos_manos.svg';
import Dos_Manos_Selected from '../imagenes/A_Dos_Manos_Selected.svg';
import Linea from '../imagenes/Linea_seperacion.svg';
import Abrir from '../imagenes/Abrir.svg';
import Cerrar from '../imagenes/Cerrar.svg';
import Bloquear from '../imagenes/Bloquear.svg';
import Desbloquear from '../imagenes/Desbloquear.svg';
import Posiciones_preestablecidas from '../imagenes/Posiciones_preestablecidas.svg';

import { useHistory } from 'react-router';

function Sillones(){
  
  const history = useHistory();

  const [bloqueado, setBloqueado] = useState(false);
  const [imageBloq, setImageBloq] = useState(Bloquear);
  
  const [manos, setManos] = useState(false);
  const [imgUnaMano, setImgUnaMano] = useState(Una_Mano_Selected);
  const [imgDosManos, setImgDosManos] = useState(Dos_Manos);

  const [connectedDeviceId, setConnectedDeviceId] = useState(null);

  const [presionado, setPresionado] = useState(false);

  const HEART_RATE_SERVICE = '143fda37-5a0e-4ebb-9a3c-fc0e7625f086';
  const CHAR_MODE_UUID = "24f56a5b-8943-47b4-aab7-96a3ef03469d"
  const CHAR_DIR_UUID = "ceba6cdc-8e39-42f1-a8b8-fca3832014fe"
  const CHAR_LOCK_UUID = "dd163077-eec7-4473-901d-5c336ecc453e"

  const openWeb = () => {
    window.open('https://www.fama.es/sensae', '_blank', 'location=yes');
  };



  async function unaMano(){
    console.log("CAMBIO A UNA MANO");

    try {

      if(BleClient.getConnectedDevices([]) === 0){
      
        history.push('/Sillones')
  
      }else{
  
        const devices = await BleClient.getConnectedDevices([]);
        console.log('Dispositivos conectados:', devices);
  
        devices.forEach(async (device) => {
          await BleClient.write(device.deviceId, HEART_RATE_SERVICE, CHAR_MODE_UUID, numbersToDataView([1]));
          console.log('written [1, 0] to control point');
  
        });
      }

    } catch (error) {
      console.error(error);
    }

    if(manos == true){
      setManos(false);
      setImgUnaMano(Una_Mano_Selected);
      setImgDosManos(Dos_Manos);
    }
  }

  async function dosManos(){
    console.log("CAMBIO A DOS MANOS");

    try {

      if(BleClient.getConnectedDevices([]) === 0){
      
        history.push('/Sillones')
  
      }else{
  
        const devices = await BleClient.getConnectedDevices([]);
        console.log('Dispositivos conectados:', devices);
  
        devices.forEach(async (device) => {
          await BleClient.write(device.deviceId, HEART_RATE_SERVICE, CHAR_MODE_UUID, numbersToDataView([2]));
          console.log('written [1, 0] to control point');
  
        });
      }

    } catch (error) {
      console.error(error);
    }

    if(manos == false){
      setManos(true);
      setImgDosManos(Dos_Manos_Selected)
      setImgUnaMano(Una_Mano)
    }
  }

  const abrir = async () => {
    setPresionado(true);
    try {

      if(BleClient.getConnectedDevices([]) === 0){
      
        history.push('/Sillones')
  
      }else{
  
        const devices = await BleClient.getConnectedDevices([]);
        console.log('Dispositivos conectados:', devices);
  
        devices.forEach(async (device) => {
          await BleClient.write(device.deviceId, HEART_RATE_SERVICE, CHAR_DIR_UUID, numbersToDataView([1]));
          console.log('written [1, 0] to control point');
  
        });
      }

    } catch (error) {
      console.error(error);
    }
  }

  const cerrar = async () => {
    setPresionado(true);
    try {

      if(BleClient.getConnectedDevices([]) === 0){
      
        history.push('/Sillones')
  
      }else{
  
        const devices = await BleClient.getConnectedDevices([]);
        console.log('Dispositivos conectados:', devices);
  
        devices.forEach(async (device) => {
          await BleClient.write(device.deviceId, HEART_RATE_SERVICE, CHAR_DIR_UUID, numbersToDataView([-1]));
          console.log('written [1, 0] to control point');
  
        });
      }

    } catch (error) {
      console.error(error);
    }
  }

  const parar = async () => {
    setPresionado(false);
    try {

      if(BleClient.getConnectedDevices([]) === 0){
      
        history.push('/Sillones')
  
      }else{
  
        const devices = await BleClient.getConnectedDevices([]);
        console.log('Dispositivos conectados:', devices);
  
        devices.forEach(async (device) => {
          await BleClient.write(device.deviceId, HEART_RATE_SERVICE, CHAR_DIR_UUID, numbersToDataView([0]));
          console.log('written [1, 0] to control point');
  
        });
      }

    } catch (error) {
      console.error(error);
    }
  }

  async function bloquear(){
    if(bloqueado == true){
      console.log("DESBLOQUEADO");
      setBloqueado(false);
      setImageBloq(Bloquear);

      try {

        if(BleClient.getConnectedDevices([]) === 0){
        
          history.push('/Sillones')
    
        }else{
    
          const devices = await BleClient.getConnectedDevices([]);
          console.log('Dispositivos conectados:', devices);
    
          devices.forEach(async (device) => {
            await BleClient.write(device.deviceId, HEART_RATE_SERVICE, CHAR_LOCK_UUID, numbersToDataView([0]));
            console.log('written [1, 0] to control point');
    
          });
        }
  
      } catch (error) {
        console.error(error);
      }

    }else{
      console.log("BLOQUEADO");
      setBloqueado(true);
      setImageBloq(Desbloquear);

      try {

        if(BleClient.getConnectedDevices([]) === 0){
        
          history.push('/Sillones')
    
        }else{
    
          const devices = await BleClient.getConnectedDevices([]);
          console.log('Dispositivos conectados:', devices);
    
          devices.forEach(async (device) => {
            await BleClient.write(device.deviceId, HEART_RATE_SERVICE, CHAR_LOCK_UUID, numbersToDataView([1]));
            console.log('written [1, 0] to control point');
    
          });
        }
  
      } catch (error) {
        console.error(error);
      }

    }

  }

  function posiciones(){
    //history.push('/Posiciones');
  }

  return (
    <IonPage>
      <IonHeader className='toolbar'>
        <IonToolbar className='toolbar'>
        <IonTitle className='titulo3'>Sill√≥n Conectado
          <button  className='buttonInfo' onClick={openWeb}><IonIcon icon={informationCircleOutline}></IonIcon></button></IonTitle>        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>

    <h3 className='encabezados'>Modo de control</h3>
        <img src={imgUnaMano} className='imagenesManos' onClick={unaMano}></img>
        <img src={imgDosManos} className='imagenesManos' onClick={dosManos}></img>

        <img src={Linea} className='imagenLinea'></img>

    <h3 className='encabezados'>Control Manual</h3>
    <div class="contenedor-imagenes">
        <img src={Abrir} className='imegenesAbriryCerrar' onPointerDown={abrir} onPointerUp={parar} ></img>
        <img src={Cerrar} className='imegenesAbriryCerrar'onPointerDown={cerrar} onPointerUp={parar}></img>
    </div>
        <img src={Linea} className='imagenLinea'></img>

        <img src={imageBloq} className='imagenBloquear' onClick={bloquear}></img>
        <img src={Posiciones_preestablecidas} className='imagenPosiciones' onClick={posiciones}></img>

      </IonContent>

    </IonPage>
  );
};

export default Sillones;
