import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonIcon, IonButton, IonImg, IonFooter } from '@ionic/react';
import { informationCircleOutline } from 'ionicons/icons';
import { BleClient, numbersToDataView } from '@capacitor-community/bluetooth-le';
import React, { useState, useEffect } from 'react';
import './Home.css';

import { radioButtonOffOutline, chevronBackOutline } from 'ionicons/icons';


import { BLE } from '@ionic-native/ble';

import { ReactSVG } from 'react-svg';

import Una_Mano from '../imagenes/A_una_mano.svg';
import Una_Mano_Selected from '../imagenes/A_Una_Mano_Selected.svg';
import Dos_Manos from '../imagenes/A_dos_manos.svg';
import Dos_Manos_Selected from '../imagenes/A_Dos_Manos_Selected.svg';
import Linea from '../imagenes/Linea_seperacion.svg';
import Abrir from '../imagenes/Abrir.svg';
import Cerrar from '../imagenes/Cerrar.svg';
import Bloquear from '../imagenes/Bloquear.svg';
import Desbloquear from '../imagenes/Desbloquear.svg';
import Posiciones_preestablecidas from '../imagenes/Posiciones_preestablecidas.svg';

function Sillones(){
  

  const [bloqueado, setBloqueado] = useState(false);
  const [imageBloq, setImageBloq] = useState(Bloquear);
  
  const [manos, setManos] = useState(false);
  const [imgUnaMano, setImgUnaMano] = useState(Una_Mano_Selected);
  const [imgDosManos, setImgDosManos] = useState(Dos_Manos);

  const [device, setDevice] = useState(null);

  const [presionado, setPresionado] = useState(false);

  const HEART_RATE_SERVICE = '143fda37-5a0e-4ebb-9a3c-fc0e7625f086';
  const CHAR_MODE_UUID = "24f56a5b-8943-47b4-aab7-96a3ef03469d"
  const CHAR_DIR_UUID = "ceba6cdc-8e39-42f1-a8b8-fca3832014fe"
  const CHAR_LOCK_UUID = "dd163077-eec7-4473-901d-5c336ecc453e"

  const openWeb = () => {
    window.open('https://www.fama.es/sensae', '_blank', 'location=yes');
  };

  useEffect(() => {

      BLE.connectedPeripheralsWithServices([HEART_RATE_SERVICE]).then(
        async peripherals => {
          console.log('Dispositivos conectados con el servicio ' + HEART_RATE_SERVICE + ':');
          for(let i = 0; i < peripherals.length; i++) {
            console.log(peripherals[i].id);
          }
        },
        error => {
          console.log('Error al buscar dispositivos conectados: ' + error);
        }
    );
    
    setTimeout(buscar, 10);

    function buscar(){

      BLE.connectedPeripheralsWithServices([HEART_RATE_SERVICE]).then(
        async peripherals => {
          console.log('Dispositivos conectados con el servicio ' + HEART_RATE_SERVICE + ':');
          for(let i = 0; i < peripherals.length; i++) {
            console.log(peripherals[i].id);
            const estado = await BleClient.read(peripherals[i].id, HEART_RATE_SERVICE, CHAR_MODE_UUID);
            console.log('ESTADO:', estado.getUint8(0));
            if(estado.getUint8(0) == 1){
              console.log('UNA MANOO!!:', estado);
              setManos(false);
              setImgUnaMano(Una_Mano_Selected);
              setImgDosManos(Dos_Manos);
            }else if(estado.getUint8(0) == 2){
              console.log('DOS MANOOSSS!!:', estado);
              setManos(true);
              setImgDosManos(Dos_Manos_Selected);
              setImgUnaMano(Una_Mano);
            }
  
            const estado2 = await BleClient.read(peripherals[i].id, HEART_RATE_SERVICE, CHAR_LOCK_UUID);
            console.log('ESTADO:', estado2.getUint8(0));
            if(estado2.getUint8(0) == 0){
              setBloqueado(false);
              setImageBloq(Bloquear);
            }else if(estado2.getUint8(0) == 1){
              setBloqueado(true);
              setImageBloq(Desbloquear);
            }
  
          }
        },
        error => {
          console.log('Error al buscar dispositivos conectados: ' + error);
        }  
      );
    }

    const interval = setInterval(() => {

      BLE.connectedPeripheralsWithServices([HEART_RATE_SERVICE]).then(
        async peripherals => {
          console.log('Dispositivos conectados con el servicio ' + HEART_RATE_SERVICE + ':');
          for(let i = 0; i < peripherals.length; i++) {
            console.log(peripherals[i].id);
          }

          if(peripherals.length <= 0){
            window.location.href ='/Sillones';
          }

        },
        error => {
          console.log('Error al buscar dispositivos conectados: ' + error);
        }
      );

    }, 2000);

  }, []);

  async function unaMano(){
    console.log("CAMBIO A UNA MANO");

    try {

      if(BleClient.getConnectedDevices([]) === 0){
      
        window.location.href ='/Sillones';
  
      }else{
  
        BLE.connectedPeripheralsWithServices([HEART_RATE_SERVICE]).then(
          async peripherals => {
            console.log('Dispositivos conectados con el servicio ' + HEART_RATE_SERVICE + ':');
            for(let i = 0; i < peripherals.length; i++) {
              console.log(peripherals[i].id);
              await BleClient.write(peripherals[i].id, HEART_RATE_SERVICE, CHAR_MODE_UUID, numbersToDataView([1]));
              console.log('Ecrito correctamente');
            }
          },
          error => {
            console.log('Error al buscar dispositivos conectados: ' + error);
          }
        );

      }

    } catch (error) {
      console.error(error);
    }

    if(manos == true){
      setManos(false);
      setImgUnaMano(Una_Mano_Selected);
      setImgDosManos(Dos_Manos);
    }
  }

  async function dosManos(){
    console.log("CAMBIO A DOS MANOS");

    try {

      if(BleClient.getConnectedDevices([]) === 0){
      
        window.location.href ='/Sillones';
  
      }else{
  
        BLE.connectedPeripheralsWithServices([HEART_RATE_SERVICE]).then(
          async peripherals => {
            console.log('Dispositivos conectados con el servicio ' + HEART_RATE_SERVICE + ':');
            for(let i = 0; i < peripherals.length; i++) {
              console.log(peripherals[i].id);
              await BleClient.write(peripherals[i].id, HEART_RATE_SERVICE, CHAR_MODE_UUID, numbersToDataView([2]));
              console.log('Ecrito correctamente');
            }
          },
          error => {
            console.log('Error al buscar dispositivos conectados: ' + error);
          }
        );
      }

