import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonIcon, IonButton, IonList, IonItem, IonFooter, IonAlert, IonInput } from '@ionic/react';
import { radioButtonOffOutline, chevronBackOutline, informationCircleOutline } from 'ionicons/icons';
import React, { useEffect, useState, useRef } from 'react';
import { BleClient, ScanMode, numbersToDataView } from '@capacitor-community/bluetooth-le';
import { useHistory } from 'react-router-dom';
import { ReactSVG } from 'react-svg';

import './Home.css';
import SillonTocarBrazo from '../imagenes/SillonTocarBrazo.svg';
import copyright from '../imagenes/Copyright.svg';
import logoFama from '../imagenes/LogoFama.svg';
import Bocadillo from '../imagenes/Bocadillo.svg';

const HEART_RATE_SERVICE = '143fda37-5a0e-4ebb-9a3c-fc0e7625f086';
const CHAR_DIR_UUID = "ceba6cdc-8e39-42f1-a8b8-fca3832014fe"

function Sillones(){

  const [scanning, setScanning] = useState(false);
  const [scanResults, setScanResults] = useState([]);
  const [scanningInterval, setScanningInterval] = useState(null);

  const [showImage, setShowImage] = useState(false);
  const [connectedDeviceId, setConnectedDeviceId] = useState(null);
  const [deviceName, setDeviceName] = useState(null);

  const [inputValue, setInputValue] = useState(null);
  const [id1, setId1] = useState(null);
  const inputRef = useRef(null);

  const handleButtonPress = () => {
      document.activeElement.blur();
      localStorage.setItem(id1, inputValue);
  };

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  function buscarNombre(id){

    setDeviceName(localStorage.getItem(id));
    console.log('ESTE ES EL NOMBRE DEL DEVICE: ', deviceName);
    console.log('Este es el id', id);
    setId1(id);


  }

  const openWeb = () => {
    window.open('https://www.fama.es/sensae', '_blank', 'location=yes');
  };

  useEffect(() => {
    BleClient.initialize();
    startScan();
  }, []);

  async function doScan(callback) {
    const device = await BleClient.requestLEScan({
      services: [HEART_RATE_SERVICE],
      scanMode: ScanMode.SCAN_MODE_LOW_LATENCY,
    }, callback);
  }

  function startScan() {
    setScanResults([]);
    setScanning(true);
    doScan(result => setScanResults(scanResults => [...scanResults, result]))
      .finally(() => setScanning(false));
  }

  async function connectToDevice(deviceId) {
    try {

      await BleClient.connect(deviceId, (deviceId) => onDisconnect(deviceId));
      setConnectedDeviceId(deviceId);

      if(BleClient.isEnabled){
        BleClient.stopLEScan();
        window.location.href = '/Movimiento';
      }

    } catch (error) {
      console.error(error);
      recargar();
    }
  }

  async function onDisconnect(deviceId) {
    
    console.log(`Device disconnected: ${deviceId}`);
    setConnectedDeviceId(null);
    // Eliminar el dispositivo de la lista de resultados de escaneo
    setScanResults(scanResults => {
      const filteredResults = scanResults.filter(r => r.device.deviceId !== deviceId);
      return filteredResults;
    });


  }

  function Mostrar(){

    if(scanResults == 0){
      setShowImage(false);
      return(

            <IonContent fullscreen>
                
                {!showImage && <ReactSVG src={Bocadillo} className='imagenBocadillo'></ReactSVG>}
                {!showImage && <ReactSVG src={SillonTocarBrazo} className='imagenSillon'></ReactSVG>}

            </IonContent>
        )
    }else{
      setShowImage(true);

      return(
        
        <IonContent fullscreen>
            
            {!showImage && <ReactSVG src={Bocadillo} className='imagenBocadillo'></ReactSVG>}
            {!showImage && <ReactSVG src={SillonTocarBrazo} className='imagenSillon'></ReactSVG>}

            <IonList className='Lista' lines="none">
              {scanResults.map((result) => (
                <IonItem className='Item' key={result.device.deviceId}>
                  {buscarNombre(result.device.deviceId)}
                  <button className='ButtonDispositivos' onClick={() => connectToDevice(result.device.deviceId)}>{inputValue ?? 'FAMA ' + result.device.deviceId.substr(-4)}</button>
                    
                    <IonInput onIonChange={handleInputChange}></IonInput>
                    <IonButton onClick={()=>handleButtonPress}>Change name</IonButton>
                    
                </IonItem>
              ))}
          </IonList>

        </IonContent>
      )
    }

  }
       
  function cambiarNombre(id){

    const name = inputValue;

    console.log("El nombre nuevo es: ", name)
    
    localStorage.setItem(id, name);

    console.log('ESTE ES EL NOMBRE DEL DEVICE: ', deviceName)

  }

  function recargar(){
    window.location.reload();
  }

  function back(){

    window.location.href = '/Home'

  }

  function closed(){

    window.location.href = '/Home'

  }

  return (
    <IonPage>
      <IonHeader className='toolbar'>
        <IonToolbar className='toolbar'>
          <IonTitle className='titulo2'>Selecciona tu Sill√≥n <button  className='buttonInfo' onClick={openWeb}><IonIcon icon={informationCircleOutline}></IonIcon></button> </IonTitle>
        </IonToolbar>
      </IonHeader>

      <Mostrar/>
      <IonFooter>
        <IonToolbar>
          <ReactSVG src={logoFama} className='imagenLogo'></ReactSVG>
          <ReactSVG src={copyright} className='imagenCopy'></ReactSVG>  
          <div className='contenedor-imagenes'>
            <IonIcon icon={radioButtonOffOutline} className='iconClose' onClick={closed}></IonIcon>
            <IonIcon icon={chevronBackOutline} className='iconBack' onClick={back}></IonIcon>
          </div>      
        </IonToolbar>
      </IonFooter>

    </IonPage>
  );
  
};

export default Sillones;
