import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonIcon, IonButton, IonList, IonItem, IonFooter } from '@ionic/react';
import { informationCircleOutline } from 'ionicons/icons';
import { createSharp } from 'ionicons/icons';
import React, { useEffect, useState } from 'react';
import { BleClient, ScanMode, numbersToDataView } from '@capacitor-community/bluetooth-le';
import { useHistory } from 'react-router-dom';

import './Home.css';
import SillonTocarBrazo from '../imagenes/SillonTocarBrazo.svg';
import copyright from '../imagenes/Copyright.svg';
import logoFama from '../imagenes/LogoFama.svg';
import Bocadillo from '../imagenes/Bocadillo.svg';

const HEART_RATE_SERVICE = '143fda37-5a0e-4ebb-9a3c-fc0e7625f086';
const CHAR_DIR_UUID = "ceba6cdc-8e39-42f1-a8b8-fca3832014fe"


function Sillones(){

  const [scanning, setScanning] = useState(false);
  const [scanResults, setScanResults] = useState([]);
  const [scanningInterval, setScanningInterval] = useState(null);

  const [showImage, setShowImage] = useState(false);
  const [connectedDeviceId, setConnectedDeviceId] = useState(null);

  const history = useHistory();

  const openWeb = () => {
    window.open('https://www.fama.es/sensae', '_blank', 'location=yes');
  };

  useEffect(() => {
    BleClient.initialize();
    startScan();
  }, []);


  async function doScan(callback) {
    const device = await BleClient.requestLEScan({
      services: [HEART_RATE_SERVICE],
      scanMode: ScanMode.SCAN_MODE_LOW_LATENCY,
    }, callback);
  }

  function startScan() {
    setScanResults([]);
    setScanning(true);
    doScan(result => setScanResults(scanResults => [...scanResults, result]))
      .finally(() => setScanning(false));
    
  }

  async function connectToDevice(deviceId) {
    try {

      await BleClient.connect(deviceId, (deviceId) => onDisconnect(deviceId));
      setConnectedDeviceId(deviceId);

      if(BleClient.isEnabled){
        BleClient.stopLEScan();
        window.location.href = '/Movimiento';
      }

    } catch (error) {
      console.error(error);
      recargar();
    }
  }

  async function onDisconnect(deviceId) {
    
    console.log(`Device disconnected: ${deviceId}`);
    setConnectedDeviceId(null);
    // Eliminar el dispositivo de la lista de resultados de escaneo
    setScanResults(scanResults => {
      const filteredResults = scanResults.filter(r => r.device.deviceId !== deviceId);
      return filteredResults;
    });


  }

  function Mostrar(){

    if(scanResults == 0){
      setShowImage(false);
      return(

            <IonContent fullscreen>
                
                {!showImage && <img src={Bocadillo} className='imagenBocadillo'></img>}
                {!showImage && <img src={SillonTocarBrazo} className='imagenSillon'></img>}

            </IonContent>
        )
    }else{
      setShowImage(true);
      return(
        <IonContent fullscreen>
            
            {!showImage && <img src={Bocadillo} className='imagenBocadillo'></img>}
            {!showImage && <img src={SillonTocarBrazo} className='imagenSillon'></img>}

          <IonList className='Lista' lines="none">
              {scanResults.map((result) => (
                <IonItem className='Item' key={result.device.deviceId} onClick={() => connectToDevice(result.device.deviceId)}>
                  <button className='ButtonDispositivos'>{result.device.name ?? 'Unnamed'}</button>
                </IonItem>
              ))}
          </IonList>

        </IonContent>
      )
    }
  }

  function recargar(){
    window.location.reload();
  }

  function editarNombre(){
    console.log("CAMBIAR NOMBRE")
  }

  return (
    <IonPage>
      <IonHeader className='toolbar'>
        <IonToolbar className='toolbar'>
          <IonTitle className='titulo2'>Selecciona tu Sillón <button  className='buttonInfo' onClick={openWeb}><IonIcon icon={informationCircleOutline}></IonIcon></button> </IonTitle>
        </IonToolbar>
      </IonHeader>

      <Mostrar/>
      <IonFooter>
        <IonToolbar>
          <img src={logoFama} className='imagenLogo'></img>
          <img src={copyright} className='imagenCopy'></img>        
        </IonToolbar>
      </IonFooter>

    </IonPage>
  );
  
};

export default Sillones;
