import { IonButton, IonContent, IonToolbar, IonHeader, IonImg, IonLabel, IonPage, IonTitle, IonDatetime } from '@ionic/react';
import React, { useEffect, useState } from 'react';
import { ReactSVG } from 'react-svg';
import { BleClient, textToDataView } from '@capacitor-community/bluetooth-le';
import { BLE } from '@ionic-native/ble';

import './Temporizador.css';

import TituloTiempo from '../imagenes/titulotiempo.svg'
import Volver from '../imagenes/volver.svg'

const HEART_RATE_SERVICE = '0000abcd-0000-1000-8000-00805f9b34fb';
const WRITE_CHARACTERISTIC2 = "00004321-0000-1000-8000-00805F9B34FB"

function Home () {
  const [currentTime, setCurrentTime] = useState('');


  useEffect(() => {
    const interval = setInterval(() => {

      BLE.connectedPeripheralsWithServices([HEART_RATE_SERVICE]).then(
        async peripherals => {
          console.log('Dispositivos conectados con el servicio ' + HEART_RATE_SERVICE + ':');
          for(let i = 0; i < peripherals.length; i++) {
            console.log(peripherals[i].id);
          }

          if(peripherals.length <= 0){
            window.location.href ='/Dispositivos';
          }

        },
        error => {
          console.log('Error al buscar dispositivos conectados: ' + error);
        }
      );

    }, 1000);

    const getCurrentTime = () => {
      const date = new Date();
      const hours = date.getHours().toString().padStart(2, '0');
      const minutes = date.getMinutes().toString().padStart(2, '0');
      const timeStr = `${hours}:${minutes}`;
      setCurrentTime(timeStr);
    };

    // Obtener la hora actual al cargar el componente
    getCurrentTime();

    // Actualizar la hora actual cada minuto
    const interval2 = setInterval(() => {
      getCurrentTime();
    }, 30000);

    // Limpiar el intervalo cuando se desmonte el componente
    return () => {
      clearInterval(interval2);
    };

  }, []);

    const [selectedDate, setSelectedDate] = useState('');

    const handleDateChange = (event) => {
        setSelectedDate(event.target.value);
      };

    const handleClick1 = () => {
      BLE.connectedPeripheralsWithServices([HEART_RATE_SERVICE]).then(
        async peripherals => {
          console.log('Dispositivos conectados con el servicio ' + HEART_RATE_SERVICE + ':');
          for(let i = 0; i < peripherals.length; i++) {
            console.log(peripherals[i].id);
            await BleClient.write(peripherals[i].id, HEART_RATE_SERVICE, WRITE_CHARACTERISTIC2, textToDataView(selectedDate.slice(11, 16)));
            console.log('Fecha escrita:', selectedDate.slice(11, 16));
          }
        },
        error => {
          console.log('Error al buscar dispositivos conectados: ' + error);
        }
      );
    };  

    const handleClick2 = () => {

      const horaSelect = selectedDate.slice(11, 13);
      const minutoSelect = selectedDate.slice(14, 16);

      const horaReal = getCurrentTime.hours;;
      console.log(horaReal);
    };  

  function back(){
    window.location.href ='/Menu';
  }

  return (
    <IonPage>
        <IonHeader className='fondoTiempo'>
          <IonToolbar className='fondoTiempo'>
            <ReactSVG src={TituloTiempo}></ReactSVG>
          </IonToolbar>
        </IonHeader>

      <IonContent fullscreen className='fondoTiempo'>

        <IonLabel className='tituloSeleccionar'>Seleccione una hora: </IonLabel>

        <IonDatetime presentation='time' value={selectedDate} onIonChange={handleDateChange} className='timer'></IonDatetime>

        <div className='contenedor-buttons'>
          <IonButton onClick={handleClick1} className='buttons' color='#797979'>Abrir</IonButton>
          <IonButton onClick={handleClick2} className='buttons' color="var(--color-personalizado)">Cerrar</IonButton>
        </div>

        <ReactSVG src={Volver} className='imgAtras2' onClick={back}></ReactSVG>

      </IonContent>
    </IonPage>
  );
};

export default Home;
