import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonIcon, IonFooter } from '@ionic/react';
import { informationCircleOutline } from 'ionicons/icons';
import { ReactSVG } from 'react-svg';

import './Home.css';
import sillon from '../imagenes/sillon.svg';
import copyright from '../imagenes/Copyright.svg';
import logoFama from '../imagenes/LogoFama.svg';

const Home: React.FC = () => {

  const camb ='aaa';

  const openWeb = () => {
    window.open('https://www.fama.es/sensae', '_blank', 'location=yes');
  };

  const pasarPagina = () => {
    window.location.href = '/Sillones';
  };

  return (
    <IonPage>
          <IonHeader>
            <IonToolbar className='toolbar'>
              <IonTitle className='titulo'>
                <div className='icon-container'>
                  <span className='texto'>Sensae</span>
                  <IonIcon icon={informationCircleOutline} className='buttonInfo' onClick={openWeb}></IonIcon>
                </div>
              </IonTitle>
            </IonToolbar>
          </IonHeader>
      <IonContent fullscreen>

        <h2 className='subTitulo'>Este app es para controlar movimientos de tu sillón Fama. Para evitar errores, lo mejor es identificar cada sillón.</h2>

        <ReactSVG src={sillon} className='imagenSillon'></ReactSVG>

        <button className='buttonPonNombre' onClick={pasarPagina}>Pon un nombre a tu sillon</button>

      </IonContent>

        <IonFooter>
          <IonToolbar>
            <ReactSVG src={logoFama} className='imagenLogo'></ReactSVG>
            <h4 className='version'>v0.10.5</h4>
            <ReactSVG src={copyright} className='imagenCopy'></ReactSVG>        
          </IonToolbar>
        </IonFooter>

    </IonPage>
  );
};

export default Home;
