import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonIcon, IonButton, IonFooter } from '@ionic/react';
import { informationCircleOutline, radioButtonOffOutline, chevronBackOutline } from 'ionicons/icons';
import React, { useState, useEffect } from 'react';
import { BleClient, numbersToDataView } from '@capacitor-community/bluetooth-le';

import './Home.css';

import { BLE } from '@ionic-native/ble';

import { ReactSVG } from 'react-svg';


import Titulo_Posiciones from '../imagenes/Titulo_Posiciones.svg';
import Modo_TV from '../imagenes/Modo_TV.svg';
import Modo_siesta from '../imagenes/Modo_siesta.svg';
import Modo_lectura from '../imagenes/Modo_lectura.svg';
import Posición_cerrado from '../imagenes/Posición_cerrado.svg';

const HEART_RATE_SERVICE = '143fda37-5a0e-4ebb-9a3c-fc0e7625f086';
const CHAR_DIR_UUID = "ceba6cdc-8e39-42f1-a8b8-fca3832014fe"

function Posiciones(){

    useEffect(() => {
      const interval = setInterval(() => {

        BLE.connectedPeripheralsWithServices([HEART_RATE_SERVICE]).then(
          async peripherals => {
            console.log('Dispositivos conectados con el servicio ' + HEART_RATE_SERVICE + ':');
            for(let i = 0; i < peripherals.length; i++) {
              console.log(peripherals[i].id);
            }

            if(peripherals.length <= 0){
              window.location.href ='/Sillones';
            }

          },
          error => {
            console.log('Error al buscar dispositivos conectados: ' + error);
          }
        );

      }, 2000);

      }, []);

    const openWeb = () => {
        window.open('https://www.fama.es/sensae', '_blank', 'location=yes');
      };


    function parar(){

        try {

            BLE.connectedPeripheralsWithServices([HEART_RATE_SERVICE]).then(
                async peripherals => {
                  console.log('Dispositivos conectados con el servicio ' + HEART_RATE_SERVICE + ':');
                  for(let i = 0; i < peripherals.length; i++) {
                    console.log(peripherals[i].id);
                    await BleClient.write(peripherals[i].id, HEART_RATE_SERVICE, CHAR_DIR_UUID, numbersToDataView([0]));
                    console.log('Ecrito correctamente');

                  }
                },
                error => {
                  console.log('Error al buscar dispositivos conectados: ' + error);
                }
              );


        } catch (error) {
            console.log(error)
        }

    }      

    function modoTv(){

        try {

            BLE.connectedPeripheralsWithServices([HEART_RATE_SERVICE]).then(
                async peripherals => {
                  console.log('Dispositivos conectados con el servicio ' + HEART_RATE_SERVICE + ':');
                  for(let i = 0; i < peripherals.length; i++) {
                    console.log(peripherals[i].id);
                    await BleClient.write(peripherals[i].id, HEART_RATE_SERVICE, CHAR_DIR_UUID, numbersToDataView([1]));
                    console.log('Ecrito correctamente');

                    setTimeout(parar, 7000);

                  }
                },
                error => {
                  console.log('Error al buscar dispositivos conectados: ' + error);
                }
              );


        } catch (error) {
            console.log(error)
        }

    }  

    function modoLectura(){

        try {

            BLE.connectedPeripheralsWithServices([HEART_RATE_SERVICE]).then(
                async peripherals => {
                  console.log('Dispositivos conectados con el servicio ' + HEART_RATE_SERVICE + ':');
                  for(let i = 0; i < peripherals.length; i++) {
                    console.log(peripherals[i].id);
                    await BleClient.write(peripherals[i].id, HEART_RATE_SERVICE, CHAR_DIR_UUID, numbersToDataView([1]));
                    console.log('Ecrito correctamente');

                    setTimeout(parar, 3000);

                  }
                },
                error => {
                  console.log('Error al buscar dispositivos conectados: ' + error);
                }
              );


        } catch (error) {
            console.log(error)
        }

    }

    function modoCerrado(){

        try {

            BLE.connectedPeripheralsWithServices([HEART_RATE_SERVICE]).then(
                async peripherals => {
                  console.log('Dispositivos conectados con el servicio ' + HEART_RATE_SERVICE + ':');
                  for(let i = 0; i < peripherals.length; i++) {
                    console.log(peripherals[i].id);
                    await BleClient.write(peripherals[i].id, HEART_RATE_SERVICE, CHAR_DIR_UUID, numbersToDataView([-1]));
                    console.log('Ecrito correctamente');

                    setTimeout(parar, 13000);

                  }
                },
                error => {
                  console.log('Error al buscar dispositivos conectados: ' + error);
                }
              );


        } catch (error) {
            console.log(error)
        }

    }

    function modoSiesta(){

        try {

            BLE.connectedPeripheralsWithServices([HEART_RATE_SERVICE]).then(
                async peripherals => {
                  console.log('Dispositivos conectados con el servicio ' + HEART_RATE_SERVICE + ':');
                  for(let i = 0; i < peripherals.length; i++) {
                    console.log(peripherals[i].id);
                    await BleClient.write(peripherals[i].id, HEART_RATE_SERVICE, CHAR_DIR_UUID, numbersToDataView([1]));
                    console.log('Ecrito correctamente');

                    setTimeout(parar, 13000);

                  }
                },
                error => {
                  console.log('Error al buscar dispositivos conectados: ' + error);
                }
              );


        } catch (error) {
            console.log(error)
        }

    }

    function back(){

      window.location.href = '/Movimiento'

    }

    function closed(){
      window.location.href = '/Home'
    }


    return (
        <IonPage>
          <IonHeader className='toolbar'>
            <IonToolbar className='toolbar'>
              <IonTitle className='titulo3'>Sillón Conectado
              <button  className='buttonInfo' onClick={openWeb}><IonIcon icon={informationCircleOutline}></IonIcon></button></IonTitle>
            </IonToolbar>
          </IonHeader>
          <IonContent fullscreen>

            <ReactSVG src={Titulo_Posiciones} className='TituloPosiciones' ></ReactSVG>
            <ReactSVG src={Modo_lectura} className='imagenesPosiciones' onClick={modoLectura}></ReactSVG>
            <ReactSVG src={Modo_TV} className='imagenesPosiciones' onClick={modoTv}></ReactSVG>
            <ReactSVG src={Modo_siesta} className='imagenesPosiciones' onClick={modoSiesta}></ReactSVG>
            <ReactSVG src={Posición_cerrado} className='imagenesPosiciones' onClick={modoCerrado}></ReactSVG>

          </IonContent>

          <IonFooter>
            <IonToolbar>
              <div className='contenedor-imagenes'>
                <IonIcon icon={radioButtonOffOutline} className='iconClose' onClick={closed}></IonIcon>
                <IonIcon icon={chevronBackOutline} className='iconBack' onClick={back}></IonIcon>
              </div>
            </IonToolbar>
          </IonFooter>
        </IonPage>
      );

}

export default Posiciones;
